	 *
	 * @return    void
	 */
	function disable_im8_exclude_pages() {

		IM8ExcludePages::get_instance()
		               ->disable();
	} // function disable_im8_exclude_pages

	/**
	 * Enables the plugin filter.
	 *
	 * @return    void
	 */
	function enable_im8_exclude_pages() {

		IM8ExcludePages::get_instance()
		               ->enable();
	} // function enable_im8_exclude_pages

endif; // if (! class_exists('IM8ExcludePages'))